mapper.select:
select member_id
from members
where memail=#{memail}

select memail
from members
where memail=#{memail}

mapper.update:
update members
set mimageoname=#{mimageoname}, mimagetype=#{mimagetype}
where member_id=#{member_id}

update members
set  mname=#{mname}, mpassword=#{mpassword}, mphone=#{mphone}
where member_id=#{member_id}
