mapper.select:
select partner_id, pname, pjoin, member_id, location_id
from partners
where member_id=#{member_id}

select partner_id
from partners
where member_id=#{member_id}

select partner_id, pname, pjoin, location_id, paccount
from partners
where member_id=#{member_id}

select pname, location_id
from partners
where member_id=#{member_id}

select location_id
from locations
where lname=#{lname}

mapper.update:
update partners
set paccount=paccount+#{paccount}
where partner_id=#{partner_id};

update partners
set paccount=paccount-#{paccount}
where partner_id=#{partner_id};
